res.cmd:
# Establecer permisos de grupo para escritura
find /opt/www/sites/BACKEND -type d -exec chmod 2775 {} \;
find /opt/www/sites/BACKEND -type f -exec chmod 664 {} \;

# Mantener apache como propietario
chown -R apache:apache /opt/www/sites/BACKEND

# Establecer SGID bit en directorios para mantener el grupo
find /opt/www/sites/BACKEND -type d -exec chmod g+s {} \;

